mapper.select:
select * from board


		${field} like '%${query}%'


	and

		categoryName = #{categoryName}



		categoryName like '%${categoryName}%'

	and

		nickName like '%%'



		nickName = #{nickName}

	and

		channelName like '%%'



		channelName = #{channelName}


	and pub = #{pub}
	order by ${order} ${desc}
	limit #{offset}, #{size}
