mapper.select:
select * from user


		${field} like '%${query}%'

	and pub = #{pub}

order by ${order} ${desc}
limit #{offset}, #{rowNum}

mapper.delete:
DELETE FROM user, category, channel, channelcategory, board using user LEFT JOIN
category on user.nickName = category.nickName LEFT JOIN channel on user.nickName = channel.nickName LEFT JOIN channelcategory on channel.title = channelcategory.title LEFT JOIN board on user.nickName=board.nickName where
user.nickName=#{nickName}
